#include <iostream>
using namespace std;

// Write your overloaded max functions here

int main() {
    int type;
    cin >> type;

    if(type == 2) {
        int a, b;
        cin >> a >> b;
        // Call max(a, b)
    } else if(type == 3) {
        int a, b, c;
        cin >> a >> b >> c;
        // Call max(a, b, c)
    } else {
        double a, b;
        cin >> a >> b;
        // Call max(a, b) for doubles
    }

    return 0;
}

#include <iostream>
using namespace std;

// Write your reverse function using pointers

int main() {
    int n;
    cin >> n;

    int arr[100];
    for(int i = 0; i < n; i++) {
        cin >> arr[i];
    }

    // Call your function

    for(int i = 0; i < n; i++) {
        cout << arr[i];
        if(i < n-1) cout << " ";
    }

    return 0;
}

