#include <iostream>
using namespace std;

int main() {
    int r, c;
    cin >> r >> c;

    // Allocate 2D array
    int** matrix = new int*[r];
    for(int i = 0; i < r; i++) {
        matrix[i] = new int[c];
    }

    // Fill matrix
    for(int i = 0; i < r; i++) {
        for(int j = 0; j < c; j++) {
            cin >> matrix[i][j];
        }
    }

    // Print matrix
    for(int i = 0; i < r; i++) {
        for(int j = 0; j < c; j++) {
            cout << matrix[i][j];
            if(j < c-1) cout << " ";
        }
        cout << endl;
    }

    // Deallocate 2D array
    for(int i = 0; i < r; i++) {
        delete[] matrix[i];
    }
    delete[] matrix;

    return 0;
}

#include <iostream>
