int main() {
    int r, c;
    cin >> r >> c;

    // Allocate original matrix
    int** matrix = new int*[r];
    for(int i = 0; i < r; i++) {
        matrix[i] = new int[c];
    }

    for(int i = 0; i < r; i++) {
        for(int j = 0; j < c; j++) {
            cin >> matrix[i][j];
        }
    }

    // Allocate transposed matrix (c x r)
    int** transposed = new int*[c];
    for(int i = 0; i < c; i++) {
        transposed[i] = new int[r];
    }

    // Transpose

    // Print transposed
    for(int i = 0; i < c; i++) {
        for(int j = 0; j < r; j++) {
            cout << transposed[i][j];
            if(j < r-1) cout << " ";
        }
        cout << endl;
    }

    // Deallocate both matrices
    for(int i = 0; i < r; i++) {
        delete[] matrix[i];
    }
    delete[] matrix;

    for(int i = 0; i < c; i++) {
        delete[] transposed[i];
    }
    delete[] transposed;

    return 0;
}

