    int** matrix = new int*[r];
    for(int i = 0; i < r; i++) {
        matrix[i] = new int[c];
        for(int j = 0; j < c; j++) {
            cin >> matrix[i][j];
        }
    }

    int row1, row2;
    cin >> row1 >> row2;

    // Swap rows (swap pointers)
    int* temp = matrix[row1];
    matrix[row1] = matrix[row2];
    matrix[row2] = temp;

    for(int i = 0; i < r; i++) {
        for(int j = 0; j < c; j++) {
            cout << matrix[i][j];
            if(j < c-1) cout << " ";
        }
        cout << endl;
    }

    for(int i = 0; i < r; i++) {
        delete[] matrix[i];
    }
    delete[] matrix;

    return 0;
}

