#include <iostream>
using namespace std;

int main() {
    int n;
    cin >> n;

    // Allocate array of pointers
    int** jagged = new int*[n];
    int* sizes = new int[n];

    // Allocate and fill each row
    for(int i = 0; i < n; i++) {
        cin >> sizes[i];
        jagged[i] = new int[sizes[i]];
        for(int j = 0; j < sizes[i]; j++) {
            cin >> jagged[i][j];
        }
    }

    // Print jagged array
    for(int i = 0; i < n; i++) {
        for(int j = 0; j < sizes[i]; j++) {
            cout << jagged[i][j];
            if(j < sizes[i]-1) cout << " ";
        }
        cout << endl;
    }

    // Deallocate properly
    for(int i = 0; i < n; i++) {
        delete[] jagged[i];
    }
    delete[] jagged;
    delete[] sizes;

    return 0;
}
